import React, {Component} from 'react';
import {View, Text, StyleSheet, TextInput, TouchableOpacity} from 'react-native';

class Registro extends Component {
    constructor(){
        super()
        this.state = {
            email: '',
            usuario: '',
            contraseña: '',
            bio: '',
            foto: ''
        }
    }

    render(){
        return(
            <View style={styles.container}>
                <Text style={styles.titulo}>Registrate</Text>

                <View>
                    <TextInput 
                        placeholder= 'Nombre de Usuario'
                        keyboardType= 'default'
                        onChangeText={ texto => this.setState({usuario : texto})}
                        value = {this.state.usuario}
                    />
                    <TextInput 
                        placeholder= 'Email'
                        keyboardType= 'email-address'
                        onChangeText={ texto => this.setState({email : texto})}
                        value = {this.state.email}
                    />
                    <TextInput 
                        placeholder= 'Contraseña'
                        keyboardType= 'default'
                        secureTextEntry = {true}
                        onChangeText={ texto => this.setState({contraseña : texto})}
                        value = {this.state.contraseña}
                    />
                    <TextInput 
                        placeholder= 'Biografía'
                        keyboardType= 'default'
                        onChangeText={ texto => this.setState({bio : texto})}
                        value = {this.state.bio}
                    />  
                    <TextInput 
                        placeholder= 'Foto de Perfil'
                        keyboardType= 'default'
                        onChangeText={ texto => this.setState({foto : texto})}
                        value = {this.state.foto}
                    />                  

                    <TouchableOpacity onPress={ () => this.registrarUsuario ( this.state.usuario, this.state.email, this.state.contraseña, this.state.bio, this.state.foto)}>
                        <Text>Registrarme</Text>
                    </TouchableOpacity>

                    <Text onPress={ () => this.props.navigation.navigate ('Inicio')}>¿Ya tenes una cuenta? Inicia Sesión</Text>
                </View>
            </View>
        )
    }
}

const styles = StyleSheet.create({
    container: {
        flex:1,
        backgroundColor: 'rgb(234,252,255)',
        alignItems: 'center',
        justifyContent: 'center'
    },
    titulo: {
        fontFamily: 'Courier',
        fontSize: 22,
        margin: 20
    },
    form:{
        backgroundColor: 'rgb(94, 171, 194)',
        borderRadius: 10,
        padding: 15
    },
    campo: {
        backgroundColor: 'rgb(234,252,255)',
        fontFamily: 'Courier',
        fontSize: 14,
        margin: 8,
        borderRadius: 10,
        textAlign: 'center',
        color: 'rgb(115, 115, 115)',
        padding: 5
    },
    button: {
        fontFamily: 'Courier',
        fontSize: 14,
        margin: 10,
        backgroundColor: 'rgb(234,252,255)',
        borderRadius: 10,
        textAlign: 'center',
        padding: 5
    },
    link: {
        fontFamily: 'Courier',
        fontSize: 10,
        margin: 4,
        textAlign: 'right'
    },
    image:{
        height: 200
    }
})

export default Registro;
